import React, { Component } from "react";
import "./Quotebox.js";
import "./App.css";
import Button from "./components/Button.js";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [],
      selectedQuoteIndex: null,
    };
    this.selectedQuoteIndex = this.selectedQuoteIndex.bind(this);
  }

  componentDidMount() {
    fetch(
      "https://gist.githubusercontent.com/natebass/b0a548425a73bdf8ea5c618149fe1fce/raw/f4231cd5961f026264bb6bb3a6c41671b044f1f4/quotes.json"
    )
      .then((data) => data.json())
      .then((quotes) =>
        this.setState({ quotes }, () => {
          this.setState({ selectedQuoteIndex: this.selectedQuoteIndex() });
        })
      );
  }

  selectedQuoteIndex() {
    if (this.quote.length) {
      return;
    }
    const rand = 0 + Math.random() * (this.quotes.length - 1 - 0);
    return rand;
  }

  render() {
    return (
      <div className="App" id="quote-box">
        <Button
          buttonDisplayName="Next Quote"
          clickHandler={this.nextQuoteClickHandler}
        />
      </div>
    );
  }
}

export default App;
