import React, { Component } from "react";
import "./Quotebox.js";
import "./App.css";
import QuoteMachine from "./components/QuoteMachine.js";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [],
      selectedQuoteIndex: null,
    };
    this.assignNewQuoteIndex = this.assignNewQuoteIndex.bind(this);
    this.selectedQuoteIndex = this.selectedQuoteIndex.bind(this);
  }

  componentDidMount() {
    fetch(
      "https://gist.githubusercontent.com/natebass/b0a548425a73bdf8ea5c618149fe1fce/raw/f4231cd5961f026264bb6bb3a6c41671b044f1f4/quotes.json"
    )
      .then((data) => data.json())
      .then((quotes) => this.setState({ quotes }, this.assignNewQuoteIndex));
  }

  get selectedQuote() {
    if (
      !this.state.quotes.length ||
      !Number.isInteger(this.state.selectedQuoteIndex)
    ) {
      return undefined;
    }
    return this.state.quotes[this.state.selectedQuoteIndex];
  }

  selectedQuoteIndex() {
    if (!this.state.quotes.length) {
      return;
    }
    const rand = Math.floor(0 + Math.random() * (this.state.quotes.length - 1));
    return rand;
  }

  assignNewQuoteIndex() {
    this.setState({ selectedQuoteIndex: this.selectedQuoteIndex() });
  }

  render() {
    console.log(this.state.selectedQuoteIndex);
    return (
      <div className="App" id="quote-box">
        <QuoteMachine
          selectedQuote={this.selectedQuote}
          assignNewQuoteIndex={this.assignNewQuoteIndex}
        />
      </div>
    );
  }
}

export default App;
